import SwiftUI\n\npublic struct FeedView: View {\n  @State private var posts: [Post] = []\n  public init() {}\n  public var body: some View {\n    NavigationView {\n      List(posts) { p in FeedRow(post: p) }\n        .navigationTitle("Pixelle")\n        .onAppear { APIClient.shared.fetchFeed { _ in } }\n    }\n  }\n}\n\nstruct FeedRow: View { let post: Post; var body: some View { VStack(alignment: .leading) { Text(post.author.name).bold(); Text(post.content) } } }\n